        CASE mod(c.total_amount_due, 1::numeric(18,0)) WHEN NULLSEQUAL 0::numeric(18,0) THEN ((c.total_amount_due)::int)::varchar ELSE rtrim((c.total_amount_due)::varchar, '0'::varchar(1)) END AS TOTAL_AMOUNT_DUE,
