        CASE mod(c.member_cash_expense, 1::numeric(18,0)) WHEN NULLSEQUAL 0::numeric(18,0) THEN ((c.member_cash_expense)::int)::varchar ELSE rtrim((c.member_cash_expense)::varchar, '0'::varchar(1)) END AS MEMBER_CASH_EXPENSE,
