        c.occ_payment_type AS OCC_PAYMENT_TYPE,
        CASE mod(c.new_refill, 1::numeric(18,0)) WHEN NULLSEQUAL 0::numeric(18,0) THEN (c.new_refill)::varchar ELSE rtrim((c.new_refill)::varchar, '0'::varchar(1)) END AS NEW_REFILL
