          'ORGU'::varchar(4),
          CASE WHEN (G.geography_type = 'Territory'::varchar(9)) THEN 'TERR'::varchar(4) WHEN (G.geography_type = 'Region'::varchar(6)) THEN 'DIST'::varchar(4) WHEN (G.geography_type = 'Area'::varchar(4)) THEN 'REG'::varchar(3) ELSE NULL END,
