        CASE WHEN (substr(R.territory_number, 1, 3) = 'TEC'::varchar(3)) THEN 'CNS_PS'::varchar(6) WHEN (substr(R.territory_number, 1, 3) = 'TER'::varchar(3)) THEN 'RES_PS'::varchar(6) ELSE P.ref_code END AS OrganizationId,
