        de.decile AS Decile
 FROM ((((((((datamart.call_plan c JOIN ( SELECT max(call_plan.period) AS period2
 FROM datamart.call_plan) c2 ON ((c.period = c2.period2))) JOIN ( SELECT roster.territory_number,
        roster.territory_name,
