        CASE WHEN (a.status = 'New'::varchar(3)) THEN a.status WHEN (a.status = 'Delete'::varchar(6)) THEN a.status WHEN (a.change_payer_pbm = 0) THEN 'TRUE'::varchar(4) ELSE 'FALSE'::varchar(5) END AS "PBM/Payer Flag",
