        cm_ims.id_num AS IMS_ID,
        cm_vv.id_num AS VEEVA_ACCOUNT_ID,
        c.program_number AS ASI_PROGRAM_NUM,
        cvp.program_description AS PROGRAM_DESC,
        c.copay_card_number AS COPAY_CARD_NUM,
        c.physician_zip AS ZIP_CODE,
