 FROM veeva.email_activity) a
 WHERE (a.ranked = 1)) ea1 ON (((se.id = ea1.sent_email_vod__c) AND (ea1.event_type_vod__c = 'Delivered_vod'::varchar(13))))) LEFT  JOIN ( SELECT a.id,
