 FROM (datamart.ahm_interactions ints JOIN metadata.data_file_metadata dm ON ((dm.file_id = ints.file_id)))
 WHERE (ints.interactionstartdate >= add_months((now())::date, (-6)))
 GROUP BY ints.file_id,
          dm.feed_version,
