        CASE WHEN (rep.sales_force_id ~~ 'CA%'::varchar(3)) THEN (rep.oracle_string || '.6040008'::varchar(8)) ELSE (rep.oracle_string || '.7040006'::varchar(8)) END AS ADPJobCode,
